void rle_encode( const char* array, size_t n ) {
	unsigned char output[128*32];

	size_t rleSize = 1;
	size_t index = 0;
	char runValue = 0;
	char run = 0;
	while ( index < n )
	{
		if ( run )
		{
			if ( runValue >= 127 || array[index] != runValue )
			{
				// we need to write out this run!
				output[rleSize++] = runValue;
				output[rleSize++] = run;
				run = 0;
			}
		}

		run++;
		runValue = array[index];

		index++;
	}
	if ( run )
	{
		output[rleSize++] = runValue;
		output[rleSize++] = run;
		run = 0;
	}

	output[0] = rleSize;

	printf("{");
	for ( int i = 0; i < rleSize; i++ )
	{
		if ( i == 0 )
			printf("%d", output[i]);
		else
			printf(", 0x%02x",output[i]);
	}
	printf("}");

	/* int bytes = 0; */
	/* for ( int i = 2; i < rleSize; i+=2 ) */
	/* { */
	/* 	bytes += output[i]; */
	/* } */
	/* printf("\nConverting back to %d bytes\n", bytes); */
}

